rowLayout.rollMessage:
%{
  game.macros.getName("Sinless Item Roll")?.execute({ itemUuid: String(linkedEntity?.uuid ?? "") });
  return "";
}%

templateHistory.lhs:
%{
  // Prefer the actor document if CSB provides it, otherwise fall back to linkedEntity/document.
  const actorDoc =
    actor ??
    this?.actor ??
    linkedEntity?.actor ??
    linkedEntity?.document ??
    linkedEntity ??
    null;

  const aUuid = String(actorDoc?.uuid ?? "");

  if (!aUuid) {
    ui.notifications.warn("Sinless Roll Initiative: Could not resolve sheet actor UUID (linkedEntity/actor missing).");
    return "";
  }

  game.macros.getName("Sinless Roll Initiative")?.execute({ actorUuid: aUuid });
  return "";
}%

templateHistory.rhs:
%{
  var aUuid = "";
  if (typeof actor !== "undefined" && actor && actor.uuid) aUuid = String(actor.uuid);
  else if (typeof linkedEntity !== "undefined" && linkedEntity) {
    if (linkedEntity.uuid) aUuid = String(linkedEntity.uuid);
    else if (linkedEntity.document && linkedEntity.document.uuid) aUuid = String(linkedEntity.document.uuid);
    else if (linkedEntity.actor && linkedEntity.actor.uuid) aUuid = String(linkedEntity.actor.uuid);
  }

  if (!aUuid) {
    ui.notifications.warn("Sinless Roll Initiative: could not resolve actor UUID from this sheet button.");
    return "";
  }

  var m = game.macros.getName("Sinless Roll Initiative");
  if (m) m.execute({ actorUuid: aUuid });
  else ui.notifications.error('Macro "Sinless Roll Initiative" not found.');
  return "";
}%

