contents.rollMessage:
(() => {
  const macro = game.macros.getName("Sinless Item Roll");
  if (!macro) return ui.notifications?.warn("Macro 'Sinless Item Roll' not found.");

  // In an itemContainer row: linkedEntity is the item.
  // On an item sheet: entity is the item.
  const doc = linkedEntity ?? entity;
  const itemUuid = doc?.uuid;

  if (!itemUuid) return ui.notifications?.warn("No item context found (linkedEntity/entity missing).");

  return macro.execute({ itemUuid });
})()

