%{
  const SOFT_NAME = "Hacking";
  const BUTTON_LABEL = "Hack Device";

  const doc = (typeof linkedEntity !== "undefined" && linkedEntity) ? linkedEntity
            : (typeof entity !== "undefined" && entity) ? entity
            : null;

  const embeddedActorUuid =
    (doc?.parent?.documentName === "Actor" && doc?.parent?.uuid) ? String(doc.parent.uuid) : "";

  const scopeActorUuid =
    (typeof actor !== "undefined" && actor?.documentName === "Actor" && actor?.uuid) ? String(actor.uuid) : "";

  const controlledActorUuid = (() => {
    const t = canvas?.tokens?.controlled?.[0];
    return t?.actor?.uuid ? String(t.actor.uuid) : "";
  })();

  const targetedActorUuid = (() => {
    const t = game.user?.targets ? Array.from(game.user.targets)[0] : null;
    return t?.actor?.uuid ? String(t.actor.uuid) : "";
  })();

  const userCharUuid = game.user?.character?.uuid ? String(game.user.character.uuid) : "";

  const actorUuid = String(
    embeddedActorUuid ||
    scopeActorUuid ||
    controlledActorUuid ||
    targetedActorUuid ||
    userCharUuid ||
    ""
  ).trim();

  if (!actorUuid) { ui.notifications?.warn?.(`SinlessCSB: No actor context for ${BUTTON_LABEL}.`); return ""; }

  let actorDoc = await fromUuid(actorUuid);
  if (actorDoc?.documentName === "Token") actorDoc = actorDoc.actor;
  if (!actorDoc || actorDoc.documentName !== "Actor") {
    ui.notifications?.warn?.(`SinlessCSB: Could not resolve Actor for ${BUTTON_LABEL}.`);
    return "";
  }

  const soft = (actorDoc.items ?? []).find(i =>
    String(i.name ?? "").trim().toLowerCase() === SOFT_NAME.toLowerCase()
  );

  if (!soft) {
    ui.notifications?.warn?.(`Missing software "${SOFT_NAME}" on ${actorDoc.name}.`);
    return "";
  }

  const api = game.modules.get("sinlesscsb")?.api;
  if (typeof api?.rollItem !== "function") {
    ui.notifications?.error?.("SinlessCSB: rollItem API not available.");
    return "";
  }

  api.rollItem({ itemUuid: soft.uuid, actorUuid });
  return "";
}%

