contents.rollMessage:
%{
  const doc = linkedEntity ?? entity ?? rollActor ?? actor ?? null;

  // Resolve the actorUuid from the most reliable sources first.
  const actorUuid = String(
    (doc?.documentName === "Actor" ? doc.uuid : "") ||
    (doc?.parent?.documentName === "Actor" ? doc.parent.uuid : "") ||
    rollActor?.uuid ||
    actor?.uuid ||
    canvas?.tokens?.controlled?.[0]?.actor?.uuid ||
    game.user?.character?.uuid ||
    ""
  ).trim();

  if (!actorUuid) {
    console.warn("SinlessCSB | refreshPools: missing actorUuid", { linkedEntity, entity, rollActor, actor, doc });
    ui.notifications?.warn?.("SinlessCSB: refreshPools missing actorUuid (check button context).");
    return "";
  }

  const api = game.modules.get("sinlesscsb")?.api;
  if (typeof api?.refreshPools !== "function") {
    console.warn("SinlessCSB | refreshPools: API not available", { apiKeys: Object.keys(api ?? {}) });
    ui.notifications?.error?.("SinlessCSB: refreshPools API not available (module not ready / not exposed?).");
    return "";
  }

  console.log("SinlessCSB | refreshPools button", { actorUuid, docName: doc?.name, docType: doc?.documentName });
  api.refreshPools({ actorUuid });

  return "";
}%

