%{
  const doc = linkedEntity ?? entity;

  // doc may be Actor, TokenDocument, or something whose parent is Actor
  const actorUuid =
    String(doc?.uuid ?? "")?.startsWith("Actor.") ? String(doc.uuid) :
    String(doc?.actor?.uuid ?? "") ||
    String(doc?.parent?.uuid ?? "");

  if (!actorUuid) {
