  game.modules.get("sinlesscsb")?.api?.rollInitiative?.({ actorUuid });
  return "";
}%

%{
  (async () => {
    const propsPath = (k) => `system.props.${k}`;

    const num = (x, fallback = 0) => {
      const n = Number(x);
      return Number.isFinite(n) ? n : fallback;
    };

    const escapeHTML = (s) => String(s ?? "")
      .replaceAll("&", "&amp;")
      .replaceAll("<", "&lt;")
      .replaceAll(">", "&gt;")
      .replaceAll('"', "&quot;")
      .replaceAll("'", "&#039;");

    // 1) Prefer injected sheet actor if available
    let scopeActor =
      (typeof actor !== "undefined" && actor?.documentName === "Actor") ? actor : null;

    // 2) If not, resolve from CSB-stored actorUuid like "Actor.eMbezua6UThIStwT"
    if (!scopeActor) {
      const au =
        (typeof actorUuid !== "undefined" && actorUuid) ? String(actorUuid).trim() : "";

      if (au) {
        try {
          const doc = await fromUuid(au);
          if (doc?.documentName === "Actor") scopeActor = doc;
        } catch (_e) {}
      }
    }

    // 3) Only if neither sheet actor nor actorUuid works, fall back to token/target/character
    if (!scopeActor) {
      const controlledActor = canvas?.tokens?.controlled?.[0]?.actor ?? null;
      const targetedActor = (() => {
        const t = game.user?.targets ? Array.from(game.user.targets)[0] : null;
        return t?.actor ?? null;
      })();

      scopeActor = controlledActor || targetedActor || game.user?.character || null;
    }

    if (!scopeActor) {
      ui.notifications?.warn?.("Full Heal: No actor context. Open a sheet or control/target a token.");
      return;
    }

    const props = scopeActor.system?.props ?? {};
    const physicalMax = Math.max(0, Math.floor(num(props.physicalMax, 0)));
    const stunMax     = Math.max(0, Math.floor(num(props.stunMax, 0)));

    await scopeActor.update({
      [propsPath("physicalCur")]: physicalMax,
      [propsPath("stunCur")]: stunMax
    });

    ui.notifications?.info?.(`Healed ${scopeActor.name}: Physical ${physicalMax}, Stun ${stunMax}.`);

    await ChatMessage.create({
      speaker: ChatMessage.getSpeaker({ actor: scopeActor }),
      content: `
        <div class="sinlesscsb item-roll-card">
          <h3 style="margin:0 0 6px 0;">Full Heal</h3>
          <p style="margin:0 0 6px 0;"><strong>Actor:</strong> ${escapeHTML(scopeActor.name)}</p>
          <p style="margin:0 0 6px 0;"><strong>Physical:</strong> set to ${escapeHTML(physicalMax)}</p>
          <p style="margin:0 0 6px 0;"><strong>Stun:</strong> set to ${escapeHTML(stunMax)}</p>
        </div>
      `
    });
  })();

  return "";
}%

rowLayout.rollMessage:
%{
  const slot = 1;

  const doc = (typeof linkedEntity !== "undefined" && linkedEntity) ? linkedEntity
            : (typeof entity !== "undefined" && entity) ? entity
            : null;

  const itemUuid = String(doc?.uuid ?? "").trim();

  const embeddedActorUuid =
    (doc?.parent?.documentName === "Actor" && doc?.parent?.uuid) ? String(doc.parent.uuid) : "";

  const scopeActorUuid =
    (typeof actor !== "undefined" && actor?.documentName === "Actor" && actor?.uuid) ? String(actor.uuid) : "";

  const controlledActorUuid = (() => {
    const t = canvas?.tokens?.controlled?.[0];
    return t?.actor?.uuid ? String(t.actor.uuid) : "";
  })();

  const targetedActorUuid = (() => {
    const t = game.user?.targets ? Array.from(game.user.targets)[0] : null;
    return t?.actor?.uuid ? String(t.actor.uuid) : "";
  })();

  const userCharUuid = game.user?.character?.uuid ? String(game.user.character.uuid) : "";

  const actorUuid = String(
    embeddedActorUuid ||
    scopeActorUuid ||
    controlledActorUuid ||
    targetedActorUuid ||
    userCharUuid ||
    ""
  ).trim();

  if (!itemUuid) { ui.notifications?.warn?.("SinlessCSB: missing item context (itemUuid)."); return ""; }
  if (!actorUuid) { ui.notifications?.warn?.("SinlessCSB: No actor context. Control/target a token."); return ""; }

  const enabled = !!doc?.system?.props?.[`wepAction${slot}Enabled`];
  if (!enabled) { ui.notifications?.warn?.("This action is not enabled on the item."); return ""; }

  const bonus = Number(doc?.system?.props?.[`wepAction${slot}BonusDice`] ?? 0) || 0;

  const api = game.modules.get("sinlesscsb")?.api;
  if (typeof api?.rollItem !== "function") { ui.notifications?.error?.("SinlessCSB: rollItem API not available."); return ""; }

  // Pass bonus as an override (requires rollItem to support scope.bonusDiceOverride or bonusDiceDelta).
  // If your rollItem already reads item.system.props.bonusDice, you can instead write per-slot bonusDice into item props,
  // but that affects all actions. Better to pass it.
  api.rollItem({ itemUuid, actorUuid, bonusDiceDelta: bonus });

  return "";
}%

