%{
  const doc = (typeof linkedEntity !== "undefined" && linkedEntity) ? linkedEntity
            : (typeof entity !== "undefined" && entity) ? entity
            : null;

  const actorDoc = doc?.parent ?? null;
  if (!doc || actorDoc?.documentName !== "Actor") {
    ui.notifications?.warn?.("SinlessCSB: missing actor/item context.");
    return "";
  }

  const slotKey = "softwareSlot";
  const activeValue = 1;
  const inactiveValue = 0;

  const templates = new Set(["8CMq8JmyHbRCglOv", "lBNLLAeL3gcJs2RQ"]);

  const items = Array.from(actorDoc.items ?? []);
  const activeCount = items.filter(i => {
    if (!templates.has(String(i.system?.template ?? ""))) return false;
    const vRaw = i.system?.props?.[slotKey];
    const v = Number.isFinite(Number(vRaw)) ? Number(vRaw) : inactiveValue;
    return v === activeValue;
  }).length;

  let maxThreads = 0;
  for (const it of items) {
    const p = it?.system?.props ?? {};
    if (!Object.prototype.hasOwnProperty.call(p, "threadsDeck")) continue;
    const n = Number(p.threadsDeck);
    if (!Number.isFinite(n)) continue;
    if (n > maxThreads) maxThreads = Math.floor(n);
  }

  if (maxThreads > 0 && activeCount >= maxThreads) {
    ui.notifications?.warn?.(`Active Threads full (${activeCount}/${maxThreads}).`);
    return "";
  }

  const update = { "system.props.softwareSlot": activeValue };
  doc.update(update).catch((e) => console.warn("SinlessCSB | softwareSlot update failed", e));
  return "";
}%

