  ).trim();

  if (!itemUuid) { ui.notifications?.warn?.("SinlessCSB: missing item context (itemUuid)."); return ""; }
  if (!actorUuid) { ui.notifications?.warn?.("SinlessCSB: No actor context. Control/target a token."); return ""; }

  const enabled = !!doc?.system?.props?.[`wepAction${slot}Enabled`];
  if (!enabled) { ui.notifications?.warn?.("This action is not enabled on the item."); return ""; }

  const bonus = Number(doc?.system?.props?.[`wepAction${slot}BonusDice`] ?? 0) || 0;

  const api = game.modules.get("sinlesscsb")?.api;
  if (typeof api?.rollItem !== "function") { ui.notifications?.error?.("SinlessCSB: rollItem API not available."); return ""; }

  // Pass bonus as an override (requires rollItem to support scope.bonusDiceOverride or bonusDiceDelta).
  // If your rollItem already reads item.system.props.bonusDice, you can instead write per-slot bonusDice into item props,
  // but that affects all actions. Better to pass it.
  api.rollItem({ itemUuid, actorUuid, bonusDiceDelta: bonus });

  return "";
}%

