    ui.notifications?.warn?.(`Missing cyberdeck context for ${BUTTON_LABEL}.`);
    return "";
  }

  const deckProps = deck?.system?.props ?? {};
  const hackRating = Number(deckProps.hackingRating ?? deckProps.gearFeature ?? deckProps.gearFeature_copy1 ?? 0) || 0;

  const api = game.modules.get("sinlesscsb")?.api;
  if (typeof api?.rollItem !== "function") {
    ui.notifications?.error?.("SinlessCSB: rollItem API not available.");
    return "";
  }

  api.rollItem({
    itemUuid: deck.uuid,
    actorUuid,
    skillKeyOverride: "Skill_EWarfare",
    gearFeatureOverride: hackRating,
    limitBonusSourceOverride: "Hacking rating"
  });
  return "";
