  const activeMap = actorDoc?.system?.props?.activeThreads ?? {};
  const activeList = (soft?.id && activeMap?.[soft.id]) ||
    Object.values(activeMap).some(v => String(v?.name ?? "").trim().toLowerCase() === SOFT_NAME.toLowerCase());

  if (!soft || (!activeFlag && !activeList)) {
    ui.notifications?.warn?.(`Missing "${SOFT_NAME}" loaded in Active Threads on ${actorDoc.name}.`);
    return "";
  }

  const api = game.modules.get("sinlesscsb")?.api;
  if (typeof api?.rollItem !== "function") {
