%{
  // Prefer linkedEntity (inventory row) then entity (item sheet)
  const doc = (typeof linkedEntity !== "undefined" && linkedEntity) ? linkedEntity
            : (typeof entity !== "undefined" && entity) ? entity
            : null;

  const itemUuid = String(doc?.uuid ?? "").trim();

  // Actor resolution: embedded parent wins; otherwise token/character fallbacks.
  const embeddedActorUuid =
    (doc?.parent?.documentName === "Actor" && doc?.parent?.uuid) ? String(doc.parent.uuid) : "";

  const scopeActorUuid =
    (typeof actor !== "undefined" && actor?.documentName === "Actor" && actor?.uuid) ? String(actor.uuid) : "";

  const controlledActorUuid = (() => {
    const t = canvas?.tokens?.controlled?.[0];
    return t?.actor?.uuid ? String(t.actor.uuid) : "";
  })();

  const targetedActorUuid = (() => {
    const t = game.user?.targets ? Array.from(game.user.targets)[0] : null;
    return t?.actor?.uuid ? String(t.actor.uuid) : "";
  })();

  const userCharUuid = game.user?.character?.uuid ? String(game.user.character.uuid) : "";

  const actorUuid = String(
    embeddedActorUuid ||
    scopeActorUuid ||
    controlledActorUuid ||
    targetedActorUuid ||
    userCharUuid ||
    ""
  ).trim();

  if (!itemUuid) {
    ui.notifications?.warn?.("SinlessCSB: missing item context (itemUuid).");
    return "";
  }

  if (!actorUuid) {
    ui.notifications?.warn?.("SinlessCSB: No actor context. Control or target a token, then try again.");
    return "";
  }

  const api = game.modules.get("sinlesscsb")?.api;
  if (typeof api?.rollItem !== "function") {
    ui.notifications?.error?.("SinlessCSB: rollItem API not available (module not ready / not exposed).");
    return "";
  }

  api.rollItem({ itemUuid, actorUuid, bonusDiceDelta: 0 });
  return "";
}%

%{
  const slot = 3;

  const doc = (typeof linkedEntity !== "undefined" && linkedEntity) ? linkedEntity
            : (typeof entity !== "undefined" && entity) ? entity
            : null;

  const itemUuid = String(doc?.uuid ?? "").trim();

  const embeddedActorUuid =
    (doc?.parent?.documentName === "Actor" && doc?.parent?.uuid) ? String(doc.parent.uuid) : "";

  const scopeActorUuid =
    (typeof actor !== "undefined" && actor?.documentName === "Actor" && actor?.uuid) ? String(actor.uuid) : "";

  const controlledActorUuid = (() => {
    const t = canvas?.tokens?.controlled?.[0];
    return t?.actor?.uuid ? String(t.actor.uuid) : "";
  })();

  const targetedActorUuid = (() => {
    const t = game.user?.targets ? Array.from(game.user.targets)[0] : null;
    return t?.actor?.uuid ? String(t.actor.uuid) : "";
  })();

  const userCharUuid = game.user?.character?.uuid ? String(game.user.character.uuid) : "";

  const actorUuid = String(
    embeddedActorUuid ||
    scopeActorUuid ||
    controlledActorUuid ||
    targetedActorUuid ||
    userCharUuid ||
    ""
  ).trim();

  if (!itemUuid) { ui.notifications?.warn?.("SinlessCSB: missing item context (itemUuid)."); return ""; }
  if (!actorUuid) { ui.notifications?.warn?.("SinlessCSB: No actor context. Control/target a token."); return ""; }

  const enabled = !!doc?.system?.props?.[`wepAction${slot}Enabled`];
  if (!enabled) { ui.notifications?.warn?.("This action is not enabled on the item."); return ""; }

  const bonus = Number(doc?.system?.props?.[`wepAction${slot}BonusDice`] ?? 0) || 0;

  const api = game.modules.get("sinlesscsb")?.api;
  if (typeof api?.rollItem !== "function") { ui.notifications?.error?.("SinlessCSB: rollItem API not available."); return ""; }

  // Pass bonus as an override (requires rollItem to support scope.bonusDiceOverride or bonusDiceDelta).
  // If your rollItem already reads item.system.props.bonusDice, you can instead write per-slot bonusDice into item props,
  // but that affects all actions. Better to pass it.
  api.rollItem({ itemUuid, actorUuid, bonusDiceDelta: bonus });

  return "";
}%

